}

export const handleSearchText = (text) => {
    return {
        type: HANDLE_SEARCH_TEXT,
        payload: text
    }
}

export const searchTodo = () => {
    return {
        type: SEARCH_TODO,
    }
}

export const deleteItemTodo = (index) => {
    return {
        type: DELETE_ITEM_TODO,
        payload: index
    }
}
